<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Table extends CI_Controller {	 function __construct()    {        parent::__construct();		$this->load->database();    }	public function index()	{		if(isset($_SESSION['id']) || isset($_SESSION['isLoggedIn']))		{                    $bg = $this->db->query("select * from background_image");                        if($bg->num_rows()>0){                            $bg = $bg->result_array();                            $data['bg'] = $bg[0]['img_name'];                        }                        $font = $this->db->query("select * from fonts where is_active=1");            if($font->num_rows()>0){                $font = $font->result_array();                $data['fontName'] = $font[0]['name'];                $data['fontSrc'] = $font[0]['src'];            }			$this->load->view('Table_select',$data);		}		else {			redirect('login', 'refresh');		}	}}?>